    def __init__(self, x=0, y=0, number=None):
        super().__init__(bd=self._bd,
                         font=self._font)
        self.first_boom = None
        self.boom = None
        self.alarm = None
        self['image'] = None
        self.x = x
        self.y = y
        self.number = number
        self.is_bomb = False
        self.count_near_bombs = False
        self.is_open = False
        self.create_icons()

    def create_icons(self):
        """Creating icons in every button"""
        self.first_boom = PhotoImage(data=self._first_boom, format='png', master=self)
        self.boom = PhotoImage(data=self._boom, format='png', master=self)
        self.alarm = PhotoImage(data=self._alarm, format='png', master=self)
